CREATE TABLE COVID19
(   data_id          INTEGER PRIMARY KEY AUTO_INCREMENT,
    country          VARCHAR(25) NOT NULL,
    total_cases      INTEGER,
    new_cases        INTEGER,
    total_deaths     INTEGER,
    new_deaths       INTEGER,
    active_cases     INTEGER,
    total_recovered  INTEGER,
    serious_critical INTEGER,
    date_captured    TIMESTAMP
);
